/**
 * The Interface IInteger.
 */
public interface IInteger {

    /**
     * Greater than.
     *
     * @param object the object
     * @return true, if successful
     */
    boolean greaterThan( Integer object );

    /**
     * Greater than or equals.
     *
     * @param object the object
     * @return true, if successful
     */
    boolean greaterThanOrEquals( Integer object );

    /**
     * Checks if is equals.
     *
     * @param object the object
     * @return true, if is equals
     */
    boolean isEquals( Integer object );

    /**
     * Lesser than.
     *
     * @param object the object
     * @return true, if successful
     */
    boolean lesserThan( Integer object );

    /**
     * Lesser than or equals.
     *
     * @param object the object
     * @return true, if successful
     */
    boolean lesserThanOrEquals( Integer object );

}
